<!--jQuery BlockUI Plugin-->
<script type="text/javascript" src="<?php echo get_stylesheet_directory_uri() ?>/assets/js/jquery.blockUI.js"></script>
 
 <script type="text/javascript">

jQuery(function ($) {
    $('#userCaptcha').realperson({regenerate: ''});
    $( document ).ready(function() {
        $('form').trigger('AjaxSubmit');
    });
 
    $('#formhoundContactForm').submit(function(event) {
        $("#error1").css("display", "none");
        $.blockUI({ message: null });

        var selected1 = [];
        $('input[name=policy]:checked').each(function() {
            selected1.push($(this).attr('value'));
        });

        var formData = new FormData();
 
        formData.append('Country', $('#Country').val());
        formData.append('Message', $('#Message').val());
        formData.append('Email', $('#Email').val());
        formData.append('FullName', $('#FullName').val());
        formData.append('policy', selected1);
        formData.append('ipaddress', $('#ipaddress').val());
        formData.append('browser', $('#browser').val());
        formData.append('captcha', $('#userCaptcha').val());
        formData.append('HashCaptcha', $('#hashCaptcha').val());  

        $.ajax({
            type : 'post',
            url : 'http://form-hound.com/current/Inquiries/add/rg8i', 
            data : formData,
            processData: false,
            contentType: false,
            dataType : 'JSON', 
        }).done(function(data) {
            if(data.status == 200) {               
                window.location = "<?php bloginfo('url');?>/thank-you";
            } else if(data.status == 500) {
                $(document).ajaxStop($.unblockUI);
                $("#error1").css("display", "block");               
            }
            else{
                $(document).ajaxStop($.unblockUI);
                $("#error2").css("display", "block");               
            }
        });
  
        event.preventDefault();
    });
});
</script>